activitydata.script:
using System; 
using Agility.Server.Scripting.ScriptAssembly;

namespace MyNamespace
{

 public class Class1
 {
  public Class1() 
  {
  }

  [StartMethodAttribute()] 
  public void Method1(ScriptParameters sp) 
  {





Func&lt;Object, string&gt; GetInputVariableValue = x =&gt; x == null ? String.Empty : x.ToString();

string Totaalbedrag = GetInputVariableValue(sp.InputVariables[&quot;INPUT_TOTAL&quot;]);

     double TotaalBedragDouble;
	if (double.TryParse(Totaalbedrag, out TotaalBedragDouble))
            {
                Totaalbedrag = TotaalBedragDouble.ToString(&quot;####0.00&quot;);
            }
            else
            {
                TotaalBedragDouble = 0;
                Totaalbedrag = TotaalBedragDouble.ToString(&quot;####0.00&quot;); 
            }

sp.OutputVariables[&quot;TOTAL_DOUBLE&quot;] =TotaalBedragDouble;


  }
 }
}

