activitydata.script:
using System; 
using Agility.Server.Scripting.ScriptAssembly;

namespace MyNamespace
{

 public class Class1
 {
  public Class1() 
  {
  }

  [StartMethodAttribute()] 
  public void Method1(ScriptParameters sp) 
  {
	  		//
			// TODO: Add start method code here
			//
			// to check or Variable is null

			Func&lt;Object, string&gt; GetInputVariableValue = x =&gt; x == null ? String.Empty : x.ToString();

		//input variable KTA
            string BTWHoogCur = GetInputVariableValue(sp.InputVariables[&quot;BTWHOOGDEC&quot;]);
            string BTWLaagCur = GetInputVariableValue(sp.InputVariables[&quot;BTWLAAGDEC&quot;]);
            string SubtotaalCur = GetInputVariableValue(sp.InputVariables[&quot;SUBTOTAALDEC&quot;]);
            string TotaalCur = GetInputVariableValue(sp.InputVariables[&quot;TOTAALDEC&quot;]);
			string GBedrag = GetInputVariableValue(sp.InputVariables[&quot;GBEDRAG&quot;]);

            decimal BTWHoogCur_dbl;
            decimal BTWLaagCur_dbl;
            decimal SubtotaalCur_dbl;
            decimal TotaalCur_dbl;
			decimal Gbedrag_dbl;

//BTW Hoog
            if (decimal.TryParse(BTWHoogCur, out BTWHoogCur_dbl))
            {
                BTWHoogCur = BTWHoogCur_dbl.ToString(&quot;####0.00&quot;);
            }
            else
            {
                BTWHoogCur_dbl = 0;
                BTWHoogCur = BTWHoogCur_dbl.ToString(&quot;####0.00&quot;); 
            }

//BTW Laag
            if (decimal.TryParse(BTWLaagCur, out BTWLaagCur_dbl))
            {
                BTWLaagCur = BTWLaagCur_dbl.ToString(&quot;####0.00&quot;);
            }
            else
            {
                BTWLaagCur_dbl = 0;
                BTWLaagCur = BTWLaagCur_dbl.ToString(&quot;####0.00&quot;);
            }

//Totaal
            if (decimal.TryParse(TotaalCur, out TotaalCur_dbl))
            {
                TotaalCur = TotaalCur_dbl.ToString(&quot;####0.00&quot;);
            }
            else
            {
                TotaalCur_dbl = 0;
                TotaalCur = TotaalCur_dbl.ToString(&quot;####0.00&quot;);
            }

//Subtotaal
            if (decimal.TryParse(SubtotaalCur, out SubtotaalCur_dbl))
            {
                SubtotaalCur = SubtotaalCur_dbl.ToString(&quot;####0.00&quot;);
            }
            else
            {
                SubtotaalCur_dbl = 0;
                SubtotaalCur = SubtotaalCur_dbl.ToString(&quot;####0.00&quot;);
            }

//Gbedrag
            if (decimal.TryParse(GBedrag, out Gbedrag_dbl))
            {
                GBedrag = Gbedrag_dbl.ToString(&quot;####0.00&quot;);
            }
            else
            {
                Gbedrag_dbl = 0;
                GBedrag = Gbedrag_dbl.ToString(&quot;####0.00&quot;);
            }


               //Debug waardes
			 /* 
			sp.OutputVariables[&quot;BTWHOOGSTR&quot;] 	= BTWHoogCur;
            sp.OutputVariables[&quot;BTWLAAGSTR&quot;] 	= BTWLaagCur;
            sp.OutputVariables[&quot;SUBTOTAALSTR&quot;] = SubtotaalCur;
            sp.OutputVariables[&quot;TOTAALSTR&quot;] = TotaalCur;
			sp.OutputVariables[&quot;GBEDRAG&quot;] = GBedrag;
			*/

		if (Math.Round( TotaalCur_dbl, 2) != Math.Round(BTWHoogCur_dbl + BTWLaagCur_dbl + SubtotaalCur_dbl,2) )
		{
                sp.OutputVariables[&quot;ERRORMESSAGE&quot;] = &quot;Calculatie subtotaal + btw laag + btw hoog komt niet overeen met totaal bedrag&quot;;  
				sp.OutputVariables[&quot;VALID&quot;] = false; 

            }
            else
            {

				sp.OutputVariables[&quot;VALID&quot;] = true;
            }	


 }
}
}

